#[tokio::test]
async fn test_server_basic_usage() {
    use crate::*;

    async fn test_func(ctx: Context) {
        ctx.send("tcplane: 1").await.unwrap();
    }

    fn error_handle(error: String) {
        eprintln!("{}", error);
