/// Provides context handling functionality for async function types.
impl<F, Fut> AsyncFuncWithoutPin<Fut> for F
where
    F: Fn(Context) -> Fut + Send + Sync + 'static,
    Fut: Future<Output = ()> + Send + 'static,
{
}
