        }
        self
    }

    pub fn https_proxy(&mut self, host: &str, port: u16) -> &mut Self {
        if let Ok(mut config) = self.websocket.config.write() {
            config.proxy = Some(ProxyConfig {
                proxy_type: ProxyType::Https,
