    let http_port: u16 = Protocol::HTTP.get_port();
    let https_port: u16 = Protocol::HTTPS.get_port();
    let unknown_port: u16 = Protocol::Unknown("custom".to_string()).get_port();
    assert_eq!(http_port, 80);
    assert_eq!(https_port, 443);
